    public partial class BigShelfEntities : DbContext
    {
        protected override void OnModelCreating(DbModelBuilder modelBuilder)
        {
            base.OnModelCreating(modelBuilder);
            modelBuilder.Entity<Friend>()
                        .HasRequired(e => e.Profile)
                        .WithMany(e => e.Friends)
                        .HasForeignKey(e => e.ProfileId)
                        .WillCascadeOnDelete(false);
        }

        public BigShelfEntities()
            : base("name=BigShelfEntities")
        {
            if (HttpContext.Current == null)
            {
                Database.SetInitializer<BigShelfEntities>(null);
            }
            else
            {
                Database.SetInitializer<BigShelfEntities>(new BigShelfContextInitializer());
            }
        }

        public DbSet<Book> Books { get; set; }

        public DbSet<Category> Categories { get; set; }

        public DbSet<CategoryName> CategoryNames { get; set; }

        public DbSet<FlaggedBook> FlaggedBooks { get; set; }

        public DbSet<Friend> Friends { get; set; }

        public DbSet<Profile> Profiles { get; set; }
    }

